import React, { useEffect, useState } from "react";
import "./SearchBar.css";
import { useCart } from "./CartContext";

function SearchBar({ onSearch }) {
  const { cart, addToCart } = useCart();
  const [query, SetQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [selectedChemical, setSelectedChemical] = useState("");

  useEffect(() => {
    if (query.length > 1) {
      fetch(`http://127.0.0.1:5000/chemical/suggestions/${query}`)
        .then((response) => response.json())
        .then((data) => {
          console.log("Fetch Suggestions:", data);
          if (Array.isArray(data)) {
            setSuggestions(data);
          } else {
            setSuggestions([]);
          }
        })
        .catch((error) => {
          console.error("Error fetching Suggestions:", error);
        });
    } else {
      setSuggestions([]);
    }
  }, [query]);

  const handleSearch = () => {
    if (selectedChemical) {
      onSearch(selectedChemical);
    } else {
      onSearch(query);
    }
  };

  const handleSuggestionClick = (chemical) => {
    setSelectedChemical(chemical.name);
    SetQuery(chemical.name);
    setSuggestions([]);
    onSearch(chemical.name);
  };

  const handleAddToCart = (chemical) => {
    if (!addedChemicals.has(chemical.name)) {
      addToCart(chemical); // Add chemical to cart
      setAddedChemicals((prev) => new Set(prev).add(chemical.name)); // Mark as added
    }
  };

  const handleBlur = () => {
    // Delay clearing to allow the click event on suggestions to register
    setTimeout(() => {
      setSuggestions([]);
    }, 100);
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      handleSearch(query);
    }
  };

  const fetchSuggestions = async (input) => {
    if (input.length > 0) {
      try {
        const response = await fetch(
          `http://127.0.0.1:5000/chemical/suggestions/${input}`
        );
        const data = await response.json();
        console.log("Fetch Suggestions:", data); // Add this to log suggestions
        setSuggestions(data); // Ensure the suggestions state is updated
      } catch (err) {
        console.error("Error fetching suggestions:", err);
      }
    } else {
      setSuggestions([]); // Reset suggestions if input is empty
    }
  };

  return (
    <div className="search-bar-container">
      <input
        type="text"
        value={selectedChemical || query}
        onChange={(e) => {
          console.log("Typed query:", e.target.value);
          SetQuery(e.target.value);
          setSelectedChemical("");
        }}
        onKeyDown={handleKeyDown}
        onBlur={handleBlur}
        placeholder="Search for Chemical"
      />
      {suggestions.length > 0 && (
        <ul className="suggestions-list">
          {suggestions.map((chemical, index) => (
            <li key={index}>
              <span onClick={() => handleSuggestionClick(chemical)}>
                {chemical.name}
              </span>
              {addedChemicals.has(chemical.name) ? (
                <span className="checkmark">âœ”</span>
              ) : (
                <button
                  onClick={() => addToCart(chemical)} // Use addToCart from context
                  className="add-to-cart-button"
                >
                  Add to Cart
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
      <button onClick={handleSearch} className="search-button">
        Search
      </button>
    </div>
  );
}

export default SearchBar;
