import React, { useState } from "react";
import axios from "axios";
import { stripBasename } from "@remix-run/router";

function RequestChemical() {
  const [chemical, setChemical] = useState("");
  const [formula, setFormula] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();

    axios
      .post("http://127.0.0.1:5000/request", {
        chemical: "val1",
        formula: "val2",
      })
      .then((response) => {
        console.log("Data Sumbitted successfuly", response.data);
      })
      .catch((error) => {
        console.error("Error Submitting Data", error);
      });
  };

  return (
    <form onSubmit={handleSubmit} className="request">
      <input
        type="text"
        value={chemical}
        onChange={(e) => setChemical(e.target.value)}
        placeholder="Chemical Name"
      />
      <input
        type="text"
        value={formula}
        onChange={(e) => setFormula(e.target.value)}
        placeholder="Chemical Formula"
      />
      <button type="submit">Submit</button>
    </form>
  );
}

export default RequestChemical;
