import React, { useState } from 'react';
import axios from 'axios';

function SearchBar({ onSearch }) {
  const [query, setQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const handleInputChange = async (e) => {
    const value = e.target.value;
    setQuery(value);

    if (value) {
      try {
        const response = await axios.get(`http://127.0.0.1:5000/chemical/search/${value}`);
        setSuggestions(response.data); // Assuming response.data is an array of chemical objects
      } catch (err) {
        console.error('Error fetching suggestions:', err);
      }
    } else {
      setSuggestions([]); // Clear suggestions when input is empty
    }
  };

  const handleSelectSuggestion = (suggestion) => {
    setQuery(suggestion.name); // Assuming each suggestion is an object with a 'name' property
    setSuggestions([]); // Clear suggestions after selection
    onSearch(suggestion.name); // Trigger the search with the selected suggestion
  };

  return (
    <div className="search-bar-container">
      <input
        type="text"
        value={query}
        onChange={handleInputChange}
        placeholder="Search for Chemical"
      />
      <button onClick={() => onSearch(query)}>Search</button>

      {/* Suggestions Dropdown */}
      {suggestions.length > 0 && (
        <ul className="suggestions-list">
          {suggestions.map((suggestion, index) => (
            <li key={index} onClick={() => handleSelectSuggestion(suggestion)}>
              {suggestion.name} {/* Adjust based on your chemical object structure */}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default SearchBar;
