import './App.css';
import NavBar from './NavBar';
import {useState} from "react"
import axios from 'axios'
import About from './pages/About';
import Instructions from './pages/Instructions';
import RequestChemical from './pages/RequestChemical';
import Home from './pages/Home';
import {Route, Routes} from "react-router-dom";


function App() {

  const[chemical, setChemical] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async (query) => {
      try {
        const response = await axios.get(`http://127.0.0.1:5000/chemical/${query}`)
        console.log('API Response:', response.data);
        console.log('Chemical object:', chemical);
        console.log('Density:', chemical?.density);
        console.log('Safety:', chemical?.safety);
        setChemical(response.data);
        setError(null);
      } catch (err) {
        setError('Chemical not found');
        setChemical(null);
      }
    }


  return (
    <>
    <NavBar/>
    <div className= "container">
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/instructions" element={<Instructions />} />
        <Route path="/requestchemical" element={<RequestChemical />} />
      </Routes>
    </div>
    <div className="middle">
      <h2 className="title">OChem Database</h2>
    </div>
    <div className="searchbar">
      <SearchBar onSearch={handleSearch}/>
      {error && <p>{error}</p>}
      {chemical && (
        <div>
           <h2>{chemical.name}</h2>
          <p>Formula: {chemical.formula}</p>
          <p>Molecular Weight: {chemical.molWeight}</p>
          <p>Melting Point: {chemical.meltingPoint}</p>
          <p>Boiling Point: {chemical.boilingPoint}</p>
          <p>Density: {chemical.Density}</p>
          <p>Safety: {Array.isArray(chemical.Safety) && chemical.Safety.length > 0 ? chemical.Safety.join(', ') : 'Not Available'}</p>
        </div>
      )}
    </div>
    </>
  );
}

export default App;
