import './App.css';
import {useState} from "react"
import axios from 'axios'
import SearchBar from './SearchBar.js'

function App() {

  const[chemical, setChemical] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async (query) => {
    try {
      const response = await axios.get(`http://127.0.0.1:5000/chemical/${query}`)
      setChemical(response.data);
      setError(null);
    } catch (err) {
      setError('Chemical not found');
      setChemical(null);
    }
  }



  return (
    <>
    <div >
      <ul className="nav">
        <li>About</li>
        <li>Instructions</li>
        <li>Request a Chemical</li>
      </ul>
    </div>
    <div className="middle">
      <h2 className="title">Organic Chem Database</h2>
    </div>
    <div className="searchbar">
      <SearchBar onSearch={handleSearch}/>
      {error && <p>{error}</p>}
      {chemical && (
        <div>
           <h2>{chemical.name}</h2>
          <p>Formula: {chemical.formula}</p>
          <p>Molecular Weight: {chemical.molWeight}</p>
          <p>Melting Point: {chemical.meltingPoint}</p>
          <p>Boiling Point: {chemical.boilingPoint}</p>
          <p>Density: {typeof chemical.density === 'string' ? chemical.density : 'Not Available'}</p>
          <p>Safety: {chemical.safety && chemical.safety.join(', ')}</p>
        </div>
      )}
    </div>
    </>
  );
}

export default App;
