import "./App.css";
import NavBar from "./NavBar";
import About from "./pages/About";
import Instructions from "./pages/Instructions";
import RequestChemical from "./pages/RequestChemical";
import Home from "./pages/Home";
import CartPage from "./pages/CartPage"; // A page to show cart items
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { useCart } from "./CartContext";

function App() {
  return (
    <Router>
      <NavBar /> {/* Navbar stays at the top */}
      <div className="container">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/about" element={<About />} />
          <Route path="/instructions" element={<Instructions />} />
          <Route path="/request" element={<RequestChemical />} />
          <Route path="/cart" element={<CartPage />} /> {/* Cart page route */}
        </Routes>
      </div>
    </Router>
  );
}

export default App;
