import React, { useState } from "react";
import axios from "axios";
import "./TablePage.css"; // Add styles for the table if needed
import { useCart } from "../CartContext"; // Adjust the path based on your project structure

export default function TablePage() {
  const [tableData, setTableData] = useState([]);
  const [loading, setLoading] = useState(false);

  // Fetch table data from the backend
  const { cart } = useCart(); // Access the cart from CartContext

  const generateTable = async () => {
    if (cart.length === 0) {
      alert("Cart is empty! Please add items to the cart."); // Alert if cart is empty
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post(
        "http://127.0.0.1:5000/generate-table",
        {
          cart, // Use the cart data dynamically
        }
      );
      setTableData(response.data); // Populate the table with the data from the backend
    } catch (error) {
      console.error("Error generating table:", error);
    }
    setLoading(false);
  };

  // Download table as Excel
  const downloadTable = async () => {
    try {
      const response = await axios.post(
        "http://127.0.0.1:5000/export-table",
        tableData,
        {
          responseType: "blob",
        }
      );
      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement("a");
      link.href = url;
      link.setAttribute("download", "ChemicalTable.xlsx");
      document.body.appendChild(link);
      link.click();
    } catch (error) {
      console.error("Error exporting table:", error);
    }
  };

  return (
    <div className="table-page">
      <button onClick={generateTable} disabled={loading}>
        {loading ? "Loading..." : "Generate Table"}
      </button>
      {tableData.length > 0 && (
        <div>
          <table className="chemical-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Molecular Weight</th>
                <th>mmol</th>
                <th>Equivalents</th>
                <th>Melting/Boiling Point</th>
                <th>Density</th>
                <th>Hazards</th>
              </tr>
            </thead>
            <tbody>
              {tableData.map((row, index) => (
                <tr key={index}>
                  <td>{row.Name}</td>
                  <td>{row["Molecular Weight"]}</td>
                  <td>{row.mmol}</td>
                  <td>{row.Equivalents}</td>
                  <td>{row["Melting Point/Boiling Point"]}</td>
                  <td>{row.Density}</td>
                  <td>{row.Hazards}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <button onClick={downloadTable}>Download Table</button>
        </div>
      )}
    </div>
  );
}
