import { useState } from "react";
import axios from "axios";
import SearchBar from "../SearchBar";
import "./Home.css"; // Import CSS file for styling

export default function Home() {
  const [chemical, setChemical] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async (query) => {
    try {
      const response = await axios.get(
        `http://127.0.0.1:5000/chemical/${query}`
      );
      console.log("API Response:", response.data);
      setChemical(response.data);
      setError(null);
    } catch (err) {
      setError("Chemical not found");
      setChemical(null);
    }
  };

  return (
    <div className="home-container">
      <div className="searchbar">
        <h1 className="title">OChem Database</h1>
        <SearchBar onSearch={handleSearch} />
        {error && <p className="error">{error}</p>}
      </div>

      {chemical && (
        <div className="chemical-card">
          <h2>{chemical.name}</h2>
          <p className="info">
            <span>Formula:</span> {chemical.formula}
          </p>
          <p className="info">
            <span>Molecular Weight:</span> {chemical.molWeight} g/mol
          </p>
          <p className="info">
            <span>Melting Point:</span> {chemical.meltingPoint} °C
          </p>
          <p className="info">
            <span>Boiling Point:</span> {chemical.boilingPoint} °C
          </p>
          <p className="info">
            <span>Density:</span> {chemical.Density} g/cm³
          </p>
          <div className="safety">
            <h3>Safety Information:</h3>
            {Array.isArray(chemical.Safety) && chemical.Safety.length > 0 ? (
              <ul>
                {chemical.Safety.map((item, index) => (
                  <li key={index}>{item}</li>
                ))}
              </ul>
            ) : (
              <p>Not Available</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
