import React, { useEffect, useState } from "react";
import "./SearchBar.css";

function SearchBar({ onSearch }) {
  const [query, SetQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [selectedChemical, setSelectedChemical] = useState("");

  useEffect(() => {
    if (query.length > 1) {
      fetch("http://127.0.0.1:5000/chemical/suggestions/${query}")
        .then((response) => response.json())
        .then((data) => {
          if (Array.isArray(data)) {
            setSuggestions(data);
          } else {
            setSuggestions([]);
          }
        })
        .catch((error) => {
          console.error("Error fetching Suggestions:", error);
        });
    } else {
      setSuggestions([]);
    }
  }, [query]);

  const handleSearch = () => {
    if (selectedChemical) {
      onSearch(selectedChemical);
    } else {
      onSearch(query);
    }
  };

  const handleSuggestionClick = (chemical) => {
    setSelectedChemical(chemical.nam);
    setSuggestions([]);
  };

  const handleKeyDown = (event) => {
    if (event.key === "Enter") {
      handleSearch(query);
    }
  };

  return (
    <div>
      <input
        className="search-bar"
        type="text"
        value={selectedChemical || query}
        onChange={(e) => {
          SetQuery(e.target.value);
          setSelectedChemical("");
        }}
        onKeyDown={handleKeyDown}
        placeholder="Search for Chemical"
      />
      {suggestions.length > 0 && (
        <ul className="suggestions-list">
          {suggestions.map((chemical, index) => (
            <li key={index} onClick={() => handleSuggestionClick(chemical)}>
              {chemical.name}
            </li>
          ))}
        </ul>
      )}
      <button onClick={handleSearch} className="search-button">
        Search
      </button>
    </div>
  );
}

export default SearchBar;
